# Combien y a-t-il de journalistes dans ce array ?
nombre_journalistes = handles.length
puts "Nombre de journalistes : #{nombre_journalistes}"

# Combien d'handle contiennent un numéro ?
handles_numeriques = handles.select { |handle| handle =~ /\d/ }
nombre_handles_numeriques = handles_numeriques.length
puts "Nombre d'handles contenant un numéro : #{nombre_handles_numeriques}"

# Combien d'handle contiennent les 4 lettres du prénom "aude" à la suite ?
handles_aude = handles.select { |handle| handle =~ /aude/i }
nombre_handles_aude = handles_aude.length
puts "Nombre d'handles contenant 'aude' (indépendamment de la casse) : #{nombre_handles_aude}"

# Combien commencent par une majuscule (première lettre juste après le @) ?
handles_majuscule = handles.select { |handle| handle =~ /@([A-Z])\w+/ }
nombre_handles_majuscule = handles_majuscule.length
puts "Nombre d'handles commençant par une majuscule : #{nombre_handles_majuscule}"

# Combien contiennent au moins une majuscule ?
handles_avec_majuscule = handles.select { |handle| handle =~ /[A-Z]/ }
nombre_handles_avec_majuscule = handles_avec_majuscule.length
puts "Nombre d'handles contenant au moins une majuscule : #{nombre_handles_avec_majuscule}"

# Combien y a-t-il de underscore _ dans tous les pseudos confondus ?
total_underscores = handles.join("").count("_")
puts "Nombre total de underscores dans tous les pseudos : #{total_underscores}"

# Trie la liste de handle par ordre alphabétique.
handles_tries = handles.sort
puts "Handles triés par ordre alphabétique :"
puts handles_tries

# Quels sont les 50 handles les plus courts de ce array ?
handles_courts = handles.sort_by { |handle| handle.length }.take(50)
puts "Les 50 handles les plus courts :"
puts handles_courts

# Quelle est la position dans l'array de la personne @epenser ?
position_epenser = handles.index("@epenser")
puts "La position de @epenser dans l'array : #{position_epenser}"
